import React from 'react';
import styled from "styled-components";
import "../index.css"
import { NavLink } from "react-router-dom"

const OwnLink = styled(NavLink)`
  text-decoration: none;
  color: inherit;
  :visited{
    color: unset;
  }
`;

const Link = ({to, children, callback}) => {
    return(
        <OwnLink className={callback} to={to}>
            {children}
        </OwnLink>
    )
}

export default Link;
