import styled from "styled-components";
import "../index.css"
import { NavLink } from "react-router-dom"

const OwnLink = styled(c)`
  text-decoration: none;
  color: inherit;
  :visited{
    color: unset;
  }
`;

const Link = ({to, children, callback}) => {
    return(
        <NavLink className={callback} to={to}>
            {children}
        </NavLink>
    )
}

export default Link;
