import {useDispatch} from "react-redux";
import {removeContainerAction} from "../../../../store/containerReducer";

const Container = styled.div`
  background: #343434;
  border-radius: 10px;
  padding: 45px 35px 20px 35px;
  width: 456px;
  z-index: 3;
  position: relative;
`;

