import Gun from "../components/page-components/bag/images/gun.png";
import {useSelector} from "react-redux";

const defaultState = {
    bags: [{
        id: 1,
        name: "Negev",
        used: 2,
        place: 5,
        benefit: 5345.4,
        data: [{
            image: Gun,
            name: "Negev | dev_texture",
            amount: 10,
            bought_for: 0.25,
            benefit: 2.2,
            current_price: 1.75,
            percent_benefit: 46
        }, {
            image: Gun,
            name: "Рандомное название",
            amount: 1000,
            bought_for: 1213.43,
            benefit: -243,
            current_price: 0.4,
            percent_benefit: -4657
        }, {
            image: Gun,
            name: "Ооооооочень длинное название",
            amount: 1,
            bought_for: 0.22354757435,
            benefit: 212123.21314,
            current_price: 112323.71231231235,
            percent_benefit: 441241246
        },]
    }]
}

const ADD_BAG = "ADD_BAG"
const REMOVE_BAG = "REMOVE_BAG"

export const bagReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_BAG:
            return {...state, bags: [...state.bags, action.payload]}
        case REMOVE_BAG:
            return {...state, bags: state.bags.filter(bag => bag.id !== action.payload)}
        default:
            return state
    }
}

export const addBagAction = (payload) => {
    return {
        type: ADD_BAG,
        payload
    }
}

export const removeBagAction = (payload) => {
    console.log("remove", payload)
    const bags = useSelector(state => state.bags.bags)
    console.log("Bags", bags)
    return {
        type: REMOVE_BAG,
        payload
    }
}
