import React from 'react';
import styled from "styled-components";
import Logo from "../header/images/logo.png"
import Profile from "../header/images/profile.png"
import Inventory from "../header/images/inventory.png"
import Notifications from "../header/images/notifications.png"
import Tracker from "../header/images/tracker.png"
import Text from "../../theme/text";
import Image from "../../theme/image";

const HeaderWrapper = styled.div`
  background-color: #320F0F;
  padding: 13px 15px;
  display: flex;
  flex-direction: row;
  justify-content: space-between; 
  align-items: center;
  position: relative;
`;

const RowContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;


const RowContainerMod = styled(RowContainer)`
  border: 1px solid #320F0F;
  border-radius: 5px;
  padding: 8px;
  :hover {
    border: 1px solid #FFFFFF;
  }
`;

function Header() {
    return (
        <HeaderWrapper>
            <Image src={Logo}/>
            <div style={{position: "absolute", left: "0", right: "0", margin: "0 auto", width: "400px"}}>
                <RowContainer style={{columnGap: "30px"}}>
                    <RowContainerMod>
                        <Image width={40} margin={"0 10px 0 0"} src={Tracker}/>
                        <Text fontWeight={800}>
                            Investment<br/>tracker
                        </Text>
                    </RowContainerMod>
                    <RowContainerMod>
                        <Image width={40} margin={"0 8px 0 0"} src={Inventory}/>
                        <Text fontWeight={800}>
                            Inventory
                        </Text>
                    </RowContainerMod>
                </RowContainer>
            </div>
            <RowContainer style={{columnGap: "30px"}}>
                <RowContainerMod>
                    <Image width={40} margin={"0 10px 0 0"} src={Notifications}/>
                    <Text fontWeight={800}>
                        Notifications
                    </Text>
                </RowContainerMod>
                <RowContainerMod>
                    <Image width={40} margin={"0 10px 0 0"} src={Profile}/>
                    <Text fontWeight={800}>
                        Hello, uz1mane!
                    </Text>
                </RowContainerMod>
            </RowContainer>
        </HeaderWrapper>
    );
}

export default Header;
