import React from 'react';
import styled from "styled-components";
import {RowContainer} from "../components/header/header";
import Text from "../theme/text";
import Button from "../theme/button";
import Image from "../theme/image";
import Gun from "../components/page-components/bag/images/gun.png"
import PlusButton from "../components/page-components/bags/plus-button";
import Layout from "../components/layout/layout";
import Container from "../theme/container";

const ModRowContainer = styled(RowContainer)`
  justify-content: space-between;
  background-color: #DDDDDD;
  padding: 20px;
  margin-bottom: 20px;
  border-radius: 10px;
`;

/*const Data = [{
    image: Gun,
    name: "Negev | dev_texture",
    amount: 10,
    bought_for: 0.25,
    benefit: 2.2,
    current_price: 1.75,
    percent_benefit: 46
}, {
    image: Gun,
    name: "Negev | dev_texture",
    amount: 10,
    bought_for: 0.25,
    benefit: 2.2,
    current_price: 1.75,
    percent_benefit: 46
}, {
    image: Gun,
    name: "Negev | dev_texture",
    amount: 10,
    bought_for: 0.25,
    benefit: 2.2,
    current_price: 1.75,
    percent_benefit: 46
}, {
    image: Gun,
    name: "Negev | dev_texture",
    amount: 10,
    bought_for: 0.25,
    benefit: 2.2,
    current_price: 1.75,
    percent_benefit: 46
},{
    image: Gun,
    name: "Negev | dev_texture",
    amount: 10,
    bought_for: 0.25,
    benefit: 2.2,
    current_price: 1.75,
    percent_benefit: 46
},]*/

const Bag = ({data}) => {
    return (
        <Layout>
            <Container>
                <div style={{
                    backgroundColor: "#DDDDDD",
                    padding: "8px 100px 10px 15px",
                    marginBottom: "25px",
                    marginTop: "20px",
                    borderRadius: "0 0 10px 10px",
                    width: "fit-content"
                }}>
                    <Text fontSize={"20px"} color={"black"}>
                        Negevs
                    </Text>
                </div>
                {data.map((item) => (
                    <ModRowContainer>
                        <RowContainer>
                            <div style={{
                                backgroundColor: "rgba(50, 15, 15, 0.43)",
                                marginRight: "15px",
                                borderRadius: "10px"
                            }}>
                                <Image width={100} height={75} margin={"20px 10px"} src={item.image}/>
                            </div>
                            <div>
                                <div style={{
                                    backgroundColor: "#8847FF",
                                    padding: "5px 15px",
                                    borderRadius: "5px",
                                    marginBottom: "10px"
                                }}>
                                    <Text color={"white"} fontSize={"16px"}>
                                        {item.name}
                                    </Text>
                                </div>
                                <div style={{
                                    backgroundColor: "white",
                                    padding: "5px 15px",
                                    borderRadius: "5px",
                                    marginBottom: "10px"
                                }}>
                                    <Text color={"black"} fontSize={"16px"}>
                                        Amount: {item.amount}
                                    </Text>
                                </div>
                                <div style={{backgroundColor: "white", padding: "5px 15px", borderRadius: "5px"}}>
                                    <Text color={"black"} fontSize={"16px"}>
                                        Bought for: {item.bought_for}$
                                    </Text>
                                </div>
                            </div>
                        </RowContainer>
                        <div style={{backgroundColor: item.benefit >= 0 ? "#C3FEC5" : "#FEC3C3", borderRadius: "5px", padding: "8px 15px", marginLeft: "20px"}}>
                            <Text color={"black"} fontSize={"16px"}>
                                Overall benefit: {item.benefit}$
                            </Text>
                            <Text color={"black"} fontSize={"16px"}>
                                Current price: {item.current_price}$
                            </Text>
                            <Text color={"black"} fontSize={"16px"}>
                                Percent benefit: {item.percent_benefit > 0 && "+"}{item.percent_benefit}%
                            </Text>
                        </div>
                    </ModRowContainer>
                ))}
                <PlusButton/>
            </Container>
        </Layout>
    )
}

export default Bag;
