import React from 'react';
import "../index.css"
import { NavLink } from "react-router-dom"

const Link = ({to, children, header = false}) => {
    return(
        <NavLink style={{textDecoration: "none", color: "inherit", border: header ? "1px solid #320F0F" : "",
            borderRadius: header ? "5px" : ""}} to={to}>
            {children}
        </NavLink>
    )
}

export default Link;
